import 'package:flutter/material.dart';

void main() {
  runApp(
    const MaterialApp(debugShowCheckedModeBanner: false, home: CamisasScreen()),
  );
}

class CamisasScreen extends StatelessWidget {
  const CamisasScreen({super.key});

  @override
  Widget build(BuildContext context) {
    final List<Map<String, String>> times = [
      {
        "time": "Flamengo",
        "imagem":
            "data:image/jpeg;base64,",
      },
      {
        "time": "Vasco",
        "imagem":
            "data:image/jpeg;base64,",
      },
      {
        "time": "Fluminense",
        "imagem":
            "data:image/jpeg;base64,",
      },
      {
        "time": "Botafogo",
        "imagem":
            "data:image/jpeg;base64,",
      },
    ];

    return Scaffold(
      appBar: AppBar(
        title: const Text(
          "Camisas dos clubes cariocas",
          style: TextStyle(fontSize: 20),
        ),
        centerTitle: true,
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: GridView.builder(
          gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
            crossAxisCount: 2,
            crossAxisSpacing: 10,
            mainAxisSpacing: 10,
            childAspectRatio: 0.75,
          ),
          itemCount: times.length,
          itemBuilder: (context, index) {
            final time = times[index];
            return Card(
              shape: RoundedRectangleBorder(
                borderRadius: BorderRadius.circular(12),
              ),
              child: Column(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  Image.network(
                    time["imagem"]!,
                    height: 120,
                    fit: BoxFit.cover,
                  ),
                  const SizedBox(height: 10),
                  Text(
                    time["time"]!,
                    style: const TextStyle(
                      fontSize: 18,
                      fontWeight: FontWeight.bold,
                    ),
                  ),
                  const SizedBox(height: 10),
                  ElevatedButton(
                    onPressed: () {},
                    child: const Text("Avaliar"),
                  ),
                ],
              ),
            );
          },
        ),
      ),
    );
  }
}
